% Clear plot screen+clf% 2 second samples with 1/256000 distance between each sample% I you keep sample value small, plotting cosine wave may show some edges.srate = 256000; % sample rate (in Hertz)tt = 0:1/srate:2; % divide 2 seconds into 1/srate pieces% To plot the figure, % X-axis range is (-0.005, 0.002)% Y-axis range is (-1, 1)% v = [xmin xmax ymin ymax]v = [-0.003 0.020 -1 1];% axis command scales the x and y axes for our purposesaxis(v)% Produce cosine waves. % As tt is time coordinate vector, % result becomes y = f(tt).% general wave formula = wave(t) = A*cos(wt + phase)% where A = 0.75, f = 440, w = 2*pi*f, phase = 0amplitude = 0.75;freq1 = 440;freq2 = 880;example_wave1 = amplitude*cos(2*pi*freq1*tt);example_wave2 = amplitude*cos(2*pi*freq2*tt);plot(tt, example_wave1)% Plot example_wave1 for 8 periodsperiod = 8;corresponding_elements = 1:srate/freq1*period;% Plot function needs arrays for x axis and y axis.plot(tt(corresponding_elements), example_wave1(corresponding_elements))axis(v)% Plot example_wave2 for 16 periodperiod = 16;corresponding_elements = 1:srate/freq2*period;% Plot function needs arrays for x axis and y axis.plot(tt(corresponding_elements), example_wave2(corresponding_elements))axis(v)keyboardclf% Plot both together% Say hold on, then plot your graphs, then say hold offhold onaxis(v)plot(tt(corresponding_elements), example_wave1(corresponding_elements))plot(tt(corresponding_elements), example_wave2(corresponding_elements))hold offkeyboard% Plot the sum of two waveswave_sum = example_wave1 + example_wave2;plot(tt(corresponding_elements), wave_sum(corresponding_elements))v = [-0.003 0.020 -2 2];axis(v)